    const fetchBalance = async () => {
      const { data } = await getBalance();
      setBalance(data.balance);
    };

    fetchBalance();
  }, []);

  return (
    <div>
      <h2>Wallet Balance: â‚¹{balance}</h2>
    </div>
  );
}

